(186, 1, '2024-12-18 20:18:41', '2024-12-18 20:18:41', '\n/* --- General Page Styling --- */\nbody {\n    font-family: \'Arial\', sans-serif;\n    line-height: 1.6;\n    background-color: #f9f9f9;\n    color: #333;\n    margin: 0;\n    padding: 0;\n}\n\n/* --- Product Grid Layout --- */\n.products-grid {\n    display: grid;\n    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));\n    gap: 20px;\n    margin: 20px auto;\n    max-width: 1200px;\n}\n\n.product-item {\n    background-color: #fff;\n    border: 1px solid #e0e0e0;\n    border-radius: 8px;\n    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);\n    transition: transform 0.3s ease, box-shadow 0.3s ease;\n    padding: 15px;\n    text-align: center;\n}\n\n.product-item:hover {\n    transform: translateY(-5px);\n    box-shadow: 0 8px 12px rgba(0, 0, 0, 0.15);\n}\n\n/* --- Product Titles --- */\n.product-title {\n    font-size: 1.2rem;\n    font-weight: bold;\n    color: #0073aa; /* WordPress Blue */\n    margin: 10px 0;\n    text-transform: uppercase;\n}\n\n.product-title:hover {\n    color: #005177; /* Darker Blue on Hover */\n}\n\n/* --- Product Descriptions --- */\n.product-description {\n    font-size: 0.95rem;\n    color: #555;\n    margin-bottom: 15px;\n    line-height: 1.5;\n}\n\n/* --- Price Styling --- */\n.product-price {\n    font-size: 1.1rem;\n    font-weight: bold;\n    color: #28a745; /* Green for Price */\n    margin: 10px 0;\n}\n\n/* --- Add to Cart Button --- */\n.product-button {\n    display: inline-block;\n    padding: 10px 15px;\n    font-size: 0.9rem;\n    font-weight: bold;\n    text-transform: uppercase;\n    background-color: #0073aa;\n    color: #fff;\n    border: none;\n    border-radius: 5px;\n    cursor: pointer;\n    text-decoration: none;\n    transition: background-color 0.3s ease, transform 0.2s ease;\n}\n\n.product-button:hover {\n    background-color: #005177;\n    transform: scale(1.05);\n}\n\n/* --- Responsive Adjustments for Small Screens --- */\n@media (max-width: 768px) {\n    .products-grid {\n        grid-template-columns: 1fr; /* Single column layout */\n    }\n    \n    .product-item {\n        padding: 10px;\n    }\n}', 'top-store', '', 'inherit', 'closed', 'closed', '', '115-revision-v1', '', '', '2024-12-18 20:18:41', '2024-12-18 20:18:41', '', 115, 'http://localhost/?p=186', 0, 'revision', '', 0),
